        "$:/ak/plugins/EasyBookmark/newBookmark.js": {
            "text": "/*\
title: $:/ak/plugins/EasyBookmark/newBookmark.js
type: application/javascript
module-type: macro


<<newBookmark "url" "includeScreenshot" "apiflash.com APIKey">>

Example to get title from google <<getDataFromUrl  "https://google.com" "yes" "AxZfv38193DKl">>


\*/

(function(){


"use strict";



exports.name = "newBookmark";


exports.params = [{name: "url"},{name: "includeScreenshot"}, {name: "APIKey"}];

/*
Run the macro
*/

exports.run = function(url,  includeScreenshot, APIKey) {

 var url = url || "None";
 var includeScreenshot = includeScreenshot || "no";
 var tiddlerTitle = "None";
 var tiddlerText = "None";
 var APIKey = APIKey || "None";

   console.log(">>>>>>>>>>>>>>>>>> " + APIKey + "<<<<<<<<<<<<<<<<<<");


   $tw.utils.httpRequest({url: "http://textance.herokuapp.com/title/"+url,  type: "POST", callback: function (error,data){
    if (error){
    console.log("ERROR:"+error);
    }
    tiddlerTitle = data;
    tiddlerText = url;

    if(includeScreenshot == "yes"){
       tiddlerText += "<br /> <a href='" + url + "' target='_blank'>[img[https://api.apiflash.com/v1/urltoimage?access_key=" + APIKey + "&url=" + url + "]] </a>";
    }
    $tw.wiki.addTiddler({title:tiddlerTitle, text:tiddlerText});
    console.log(">>>>>>>>>>>>>>>>>> " + tiddlerText + "<<<<<<<<<<<<<<<<<<");
  }}) 
  return "[["+tiddlerTitle +"]]";
};


})();",
